import type { SVGProps } from "react";

type IconProps = SVGProps<SVGSVGElement>;

function Icon({ d, ...props }: IconProps & { d: string }) {
  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 256 256"
      width="1em"
      height="1em"
      fill="currentColor"
      {...props}
    >
      <path d={d} />
    </svg>
  );
}

